import { BlendFunction } from 'postprocessing';
import { EffectConstructor } from '@react-three/postprocessing';
import { Node } from '@react-three/fiber';

export type EffectProps<T extends EffectConstructor, Options = {}> = Node<InstanceType<T>, T> & Options & {
    blendFunction?: BlendFunction;
    opacity?: number;
};
